KakiSpikeSortingFile.commentelectrode = cell2mat(rawNumericColumns(:, 6));
KakiSpikeSortingFile.generalcomment = rawStringColumns(:, 8);
KakiSpikeSortingFile.includedtoanalysis = rawStringColumns(:, 9);
KakiSpikeSortingFile.excludedduetoelectordes = categorical(rawStringColumns(:, 10));

KakiSpikeSortingFile = table2struct(KakiSpikeSortingFile);

%% adjust output variable
for kk = 1:length(KakiSpikeSortingFile)
    matfilename{kk} = KakiSpikeSortingFile(kk).matfilename{1};
    iontophoresis{kk} = KakiSpikeSortingFile(kk).iontophoresis{1};
    isolationquality{kk} = KakiSpikeSortingFile(kk).isolationquality{1};
end

[KakiSpikeSortingFile.matfilename] = deal(matfilename{:});
[KakiSpikeSortingFile.iontophoresis] = deal(iontophoresis{:});
[KakiSpikeSortingFile.isolationquality] = deal(isolationquality{:});



